package pattern;

public class patt2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i,j,n=1;
for(int i=0;i<=9;i++) {
	for(int j=1;j<=9;j--) {
		System.out.print(" ");
	}
	for(int k=n;k<=i;k++) {
		System.out.print(" ");
	}
	System.out.println();
}
	}

}
